mapper.select:
SELECT to_id, from_id, content, time
FROM message
WHERE from_id = #{fromId}
ORDER BY time
limit #{n}

SELECT to_id, from_id, content, time
FROM message
WHERE to_id = #{toId}
ORDER BY time
limit #{n}

SELECT to_id, from_id, content, time
FROM message
WHERE to_id = #{toId} and from_id = #{fromId}
